import React from "react";

function Square(props) {
  //added constructor to class component to initialize state.
  //   constructor(props) {
  //     super(props);
  //     this.state = {
  //       value: null
  //     };
  //   }
  return (
    <button
      className='square'
      // onClick es6 arrow function so the 'alert' fires off only when clicked.
      onClick={() => {
        this.setState({ value: "X" });
      }}
    >
      {this.state.value}
    </button>
  );
}

export default Square;
